INSERT INTO `wp_calendar_config` VALUES ('calendar_style', '    .calnk a:hover {\n         background-position:0 0;\n         text-decoration:none;  \n         color:#000000;\n         border-bottom:1px dotted #000000;\n         }\n    .calnk a:visited {\n         text-decoration:none;\n         color:#000000;\n         border-bottom:1px dotted #000000;\n        }\n    .calnk a {\n        text-decoration:none; \n        color:#000000; \n        border-bottom:1px dotted #000000;\n        }\n    .calnk a span { \n        display:none; \n        }\n    .calnk a:hover span {\n        color:#333333; \n        background:#F6F79B; \n        display:block;\n        position:absolute; \n        margin-top:1px; \n        padding:5px; \n        width:150px; \n        z-index:100;\n        line-height:1.2em;\n        }\n     .calendar-table {\n        border:none;\n        width:100%;\n     }\n     .calendar-heading {\n        height:25px;\n        text-align:center;\n        border:1px solid #D6DED5;\n        background-color:#E4EBE3;\n     }\n     .calendar-next {\n        width:25%;\n        text-align:center;\n     }\n     .calendar-prev {\n        width:25%;\n        text-align:center;\n     }\n     .calendar-month {\n        width:50%;\n        text-align:center;\n        font-weight:bold;\n     }\n     .normal-day-heading {\n        text-align:center;\n        width:25px;\n        height:25px;\n        font-size:0.8em;\n        border:1px solid #DFE6DE;\n        background-color:#EBF2EA;\n     }\n     .weekend-heading {\n        text-align:center;\n        width:25px;\n        height:25px;\n        font-size:0.8em;\n        border:1px solid #DFE6DE;\n        background-color:#EBF2EA;\n        color:#FF0000;\n     }\n     .day-with-date {\n        vertical-align:text-top;\n        text-align:left;\n        width:60px;\n        height:60px;\n        border:1px solid #DFE6DE;\n     }\n     .no-events {\n\n     }\n     .day-without-date {\n        width:60px;\n        height:60px;\n        border:1px solid #E9F0E8;\n     }\n     span.weekend {\n        color:#FF0000;\n     }\n     .current-day {\n        vertical-align:text-top;\n        text-align:left;\n        width:60px;\n        height:60px;\n        border:1px solid #BFBFBF;\n        background-color:#E4EBE3;\n     }\n     span.event {\n        font-size:0.75em;\n     }\n     .kjo-link {\n        font-size:0.75em;\n        text-align:center;\n     }\n     .calendar-date-switcher {\n        height:25px;\n        text-align:center;\n        border:1px solid #D6DED5;\n        background-color:#E4EBE3;\n     }\n     .calendar-date-switcher form {\n        margin:0;\n        padding:0;\n     }\n     .calendar-date-switcher input {\n        border:1px #D6DED5 solid;\n     }\n     .calendar-date-switcher select {\n        border:1px #D6DED5 solid;\n     }\n     .cat-key {\n        width:100%;\n        margin-top:10px;\n        padding:5px;\n        border:1px solid #D6DED5;\n     }\n     .calnk a:hover span span.event-title {\n        padding:0;\n        text-align:center;\n        font-weight:bold;\n        font-size:1.2em;\n        }\n     .calnk a:hover span span.event-title-break {\n        width:96%;\n        text-align:center;\n        height:1px;\n        margin-top:5px;\n        margin-right:2%;\n        padding:0;\n        background-color:#000000;\n     }\n     .calnk a:hover span span.event-content-break {\n        width:96%;\n        text-align:center;\n        height:1px;\n        margin-top:5px;\n        margin-right:2%;\n        padding:0;\n        background-color:#000000;\n     }\n     .page-upcoming-events {\n        font-size:80%;\n     }\n     .page-todays-events {\n        font-size:80%;\n     }'); 
